#!/usr/bin/env bashset -euo pipefailmkdir -p /app/data# If S3 envs are set, fetch artifactsif [[ -n "${DATA_BUCKET:-}" && -n "${FAISS_KEY:-}" ]]; thenpython - <<'PY'import os, boto3bucket = os.environ["DATA_BUCKET"]faiss_key = os.environ.get("FAISS_KEY")db_key    = os.environ.get("DB_KEY")s3 = boto3.client("s3")def dl(k, dst):    if not k: return    os.makedirs(os.path.dirname(dst), exist_ok=True)    print(f"Downloading s3://{bucket}/{k} -> {dst}")    s3.download_file(bucket, k, dst)dl(faiss_key, "/app/data/pci_index.faiss")dl(db_key, "/app/data/pci_requirements.db")PYfi# Launch APIexec python -m uvicorn mcp_server.main:app --host 0.0.0.0 --port "${PORT:-8080}"